    <span class="token comment">//  width:"300", //图表宽度</span>
    <span class="token comment">//  height:"100", //图表高度</span>
    <span class="token literal-property property">containLabel</span><span class="token operator">:</span> <span class="token boolean">true</span><span class="token punctuation">,</span><span class="token comment">//grid 区域是否包含坐标轴的刻度标签</span>
    <span class="token comment">// show:true,//设置了true下面的containLabel以下的属性才会起作用</span>
    <span class="token comment">// backgroundColor:"pink", //网格背景色  transparent</span>
