<div class="language-javascript ext-js line-numbers-mode"><pre v-pre class="language-javascript"><code><span class="token literal-property property">tooltip</span><span class="token operator">:</span> <span class="token punctuation">{</span>
	<span class="token literal-property property">trigger</span><span class="token operator">:</span> <span class="token string">'item'</span><span class="token punctuation">,</span><span class="token comment">//不自定义此一句即可;属性值：item数据项触发、axis坐标轴触发、none不触发</span>
    <span class="token literal-property property">axisPointer</span><span class="token operator">:</span> <span class="token punctuation">{</span>
        <span class="token literal-property property">type</span><span class="token operator">:</span> <span class="token string">'line'</span><span class="token punctuation">,</span><span class="token comment">//属性值：line直线、shadow阴影、cross十字形、none</span>
        <span class="token literal-property property">axis</span><span class="token operator">:</span> <span class="token string">'auto'</span><span class="token punctuation">,</span>  <span class="token comment">//指示器坐标轴，属性值：x、y、radius、angle</span>
        <span class="token literal-property property">snap</span><span class="token operator">:</span> <span class="token boolean">true</span><span class="token punctuation">,</span>  <span class="token comment">//坐标轴指示器是否自动吸附到点上。默认自动判断，布尔值</span>
        <span class="token literal-property property">label</span><span class="token operator">:</span> <span class="token punctuation">{</span>
            <span class="token literal-property property">show</span><span class="token operator">:</span> <span class="token boolean">false</span><span class="token punctuation">,</span>  <span class="token comment">//是否显示文本标签</span>
            <span class="token literal-property property">precision</span><span class="token operator">:</span> <span class="token string">'auto'</span><span class="token punctuation">,</span> <span class="token comment">//文本标签中数值小数点精度。默认根据当前轴的值自动判断</span>
            <span class="token literal-property property">formatter</span><span class="token operator">:</span> <span class="token punctuation">{</span><span class="token punctuation">}</span><span class="token punctuation">,</span>  <span class="token comment">//文本标签文字格式化器</span>
            <span class="token literal-property property">margin</span><span class="token operator">:</span><span class="token number">5</span><span class="token punctuation">,</span>  <span class="token comment">//label距离轴的距离</span>
            <span class="token literal-property property">color</span><span class="token operator">:</span> <span class="token string">'#fff'</span><span class="token punctuation">,</span>
            <span class="token literal-property property">fontSize</span><span class="token operator">:</span> <span class="token number">12</span><span class="token punctuation">,</span>  
            <span class="token literal-property property">lineHeigh</span><span class="token operator">:</span> <span class="token number">20</span><span class="token punctuation">,</span>  
            <span class="token literal-property property">width</span><span class="token operator">:</span> <span class="token number">20</span><span class="token punctuation">,</span>  
            <span class="token literal-property property">height</span><span class="token operator">:</span> <span class="token number">40</span><span class="token punctuation">,</span> 
            <span class="token literal-property property">textBorderColor</span><span class="token operator">:</span> <span class="token string">''</span><span class="token punctuation">,</span>  <span class="token comment">//文字自己的描边颜色</span>
            <span class="token literal-property property">textBorderWidth</span><span class="token operator">:</span><span class="token number">1</span><span class="token punctuation">,</span>  <span class="token comment">//文字自己的描边宽度</span>
            <span class="token literal-property property">textBorderType</span><span class="token operator">:</span> <span class="token string">'solid'</span><span class="token punctuation">,</span> <span class="token comment">//文字自己的描边，值：solid、dashed、dotted、number、array</span>
            <span class="token literal-property property">textBorderDashOffset</span><span class="token operator">:</span> <span class="token number">0</span><span class="token punctuation">,</span><span class="token comment">//虚线偏移量，搭配textBorderType指定dashed、array</span>
            <span class="token literal-property property">textShadowColor</span><span class="token operator">:</span> <span class="token string">'transparent'</span><span class="token punctuation">,</span><span class="token comment">//文字自己阴影颜色</span>
            <span class="token literal-property property">textShadowBlur</span><span class="token operator">:</span> <span class="token number">0</span><span class="token punctuation">,</span><span class="token comment">//文字本身的阴影长度</span>
            <span class="token literal-property property">textShadowOffsetX</span><span class="token operator">:</span> <span class="token number">0</span><span class="token punctuation">,</span><span class="token comment">//文字自己的阴影 X 偏移</span>
            <span class="token literal-property property">textShadowOffsetY</span><span class="token operator">:</span> <span class="token number">0</span><span class="token punctuation">,</span><span class="token comment">//文字自己的阴影 Y 偏移</span>
            <span class="token literal-property property">overflow</span><span class="token operator">:</span> <span class="token string">'none'</span><span class="token punctuation">,</span>  <span class="token comment">//文字超出宽度是否截断或换行,配置width时有效,truncate/break/breakAll</span>
            <span class="token literal-property property">ellipsis</span><span class="token operator">:</span> <span class="token string">''</span><span class="token punctuation">,</span> <span class="token comment">//如果overflow配置为'truncate'，可以通过该属性配置末尾显示的文本</span>
            <span class="token literal-property property">padding</span><span class="token operator">:</span> <span class="token number">0</span><span class="token punctuation">,</span>  
            <span class="token literal-property property">backgroundColor</span><span class="token operator">:</span> <span class="token string">'auto'</span><span class="token punctuation">,</span><span class="token comment">//背景颜色</span>
            <span class="token literal-property property">borderColor</span><span class="token operator">:</span> <span class="token string">''</span><span class="token punctuation">,</span>  <span class="token comment">//文本标签的边框颜色</span>
            <span class="token literal-property property">borderWidth</span><span class="token operator">:</span> <span class="token number">0</span><span class="token punctuation">,</span>  <span class="token comment">//文本标签的边框宽度</span>
