        <span class="token punctuation">}</span><span class="token punctuation">,</span>
		<span class="token literal-property property">lineStyle</span><span class="token operator">:</span><span class="token punctuation">{</span>
		    <span class="token literal-property property">color</span><span class="token operator">:</span><span class="token string">'#faa12f'</span><span class="token punctuation">,</span>
            <span class="token literal-property property">width</span><span class="token operator">:</span> <span class="token number">1</span><span class="token punctuation">,</span>  <span class="token comment">//线宽</span>
            <span class="token literal-property property">type</span><span class="token operator">:</span> <span class="token string">'solid'</span><span class="token punctuation">,</span><span class="token comment">//线的类型值：solid、dashed、dotted、number、array</span>
